mapper.select:
select WAY_CODE ,AREA_NO ,WAY_TYPE ,WAY_NAME ,WAY_RANGE ,ALL_SCORE ,ORG_LEVEL ,ORG_TYPE ,WHITE_LIST ,BLACK_LIST ,START_DAT ,END_DAT ,WAY_CYCLE ,EDIT_ORG ,EDIT_ROLE ,WAY_DESC ,STATUS ,CREATE_TIME ,OPERATOR ,REVIEW_MAN ,REVIEW_TIME ,REVIEW_STATUS ,REVIEW_OPINION ,IS_EXE ,WAY_LEVEL from MO_WAY_ORG A where A.area_no = #{areaNo} AND ( ( EXISTS (SELECT 1 FROM V_AUTH_ORG WHERE AREA_NO = #{areaNo} AND USERID=#{userid} AND INSTR(','||NVL(A.EDIT_ORG,DEPTCODE)||',',','||DEPTCODE||',')>0) and EXISTS (SELECT 1 FROM pcmc_user_role WHERE USERID=#{userid} AND INSTR(','||NVL(A.EDIT_ROLE,ROLEID)||',',','||ROLEID||',')>0) AND (A.EDIT_ORG IS NOT NULL OR A.EDIT_ROLE IS NOT NULL) ) OR (A.EDIT_ORG IS NULL AND A.EDIT_ROLE IS NULL AND A.OPERATOR = #{usercode}) )
and a.way_code = #{wayCode}
and a.way_name = #{wayName}
and a.way_name like #{wayNameLike}
and a.way_type = #{wayType}
and a.start_dat >= #{startDate}
and a.way_cycle = #{wayCycle}
and a.end_dat <= #{endDate}
and a.status = #{wayStatus}
and a.status <> 'DEL' order by a.way_code

select WAY_CODE ,AREA_NO ,WAY_TYPE ,WAY_NAME ,WAY_RANGE ,ALL_SCORE ,ORG_LEVEL ,ORG_TYPE ,WHITE_LIST ,BLACK_LIST ,START_DAT ,END_DAT ,WAY_CYCLE ,EDIT_ORG ,EDIT_ROLE ,WAY_DESC ,STATUS ,CREATE_TIME ,OPERATOR ,REVIEW_MAN ,REVIEW_TIME ,REVIEW_STATUS ,REVIEW_OPINION ,IS_EXE ,WAY_LEVEL from MO_WAY_PERSON A where A.area_no = #{areaNo} AND ( ( EXISTS (SELECT 1 FROM V_AUTH_ORG WHERE A.AREA_NO = AREA_NO AND AREA_NO = #{areaNo} AND USERID=#{userid} AND INSTR(','||NVL(A.EDIT_ORG,DEPTCODE)||',',','||DEPTCODE||',')>0) and EXISTS (SELECT 1 FROM pcmc_user_role WHERE USERID=#{userid} AND INSTR(','||NVL(A.EDIT_ROLE,ROLEID)||',',','||ROLEID||',')>0) AND (A.EDIT_ORG IS NOT NULL OR A.EDIT_ROLE IS NOT NULL) ) OR (A.EDIT_ORG IS NULL AND A.EDIT_ROLE IS NULL AND A.OPERATOR = #{usercode}) )
and a.way_code = #{wayCode}
and a.way_name = #{wayName}
and a.way_name like #{wayNameLike}
and a.way_type = #{wayType}
and a.start_dat >= #{startDate}
and a.end_dat <= #{endDate}
and a.status = #{wayStatus}
and a.way_cycle = #{wayCycle}
and a.status <> 'DEL' order by way_code

    select  a.WAY_CODE,a.WAY_NAME,a.OBJTYPE from  ( 
    select  way_code,way_name,'ORG' AS objType ,edit_role,edit_org,area_no,OPERATOR
    FROM MO_WAY_ORG  WHERE STATUS='FORMAL'
    UNION
    select way_code,way_name,'PERSON' AS objType  ,edit_role,edit_org,area_no,OPERATOR
    FROM MO_WAY_PERSON WHERE STATUS='FORMAL'
    ) a 
    where  area_no = #{areaNo}
	AND (
	   (
	    EXISTS (SELECT 1 FROM V_AUTH_ORG WHERE USERID=#{userid} AND  INSTR(','||NVL(A.EDIT_ORG,DEPTCODE)||',',','||DEPTCODE||',')>0)
	    and EXISTS (SELECT 1 FROM pcmc_user_role WHERE USERID=#{userid} AND  INSTR(','||NVL(A.EDIT_ROLE,ROLEID)||',',','||ROLEID||',')>0)
	    AND (A.EDIT_ORG IS NOT NULL OR A.EDIT_ROLE IS NOT NULL)
	   ) 
	   OR (A.EDIT_ORG IS NULL  AND A.EDIT_ROLE IS NULL AND A.OPERATOR = #{usercode})
	 )

        AND A.WAY_NAME like #{wayName}


        AND A.WAY_CODE = #{wayCode}


        AND A.OBJTYPE = #{objtype}


        AND A.WAY_CODE in (#{selected})

     order by A.WAY_CODE
